mapper.sql:

	    from
			bicycle as a
			left join usercustomer b on a.userCustomer_seq = b.seq
		where 1=1
        AND a.delNy = #{shDelNy}


					AND a.cdInstDate BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.cdUpdtDate BETWEEN #{shDateStart} AND #{shDateEnd}


            AND b.userId = #{shValue}
            AND b.userName LIKE CONCAT('%',#{shValue},'%')
            AND a.bicycleName LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
insert into bicycle (
	bicycleName,
    bicycleCateCd,
    bicycleBrandCd,
    bicycleModel,
    bicycleYears,
    bicycleMiles,
    bicycleSN,
    drivetrainBrandCd,
    drivetrainModel,
    brakeBrandCd,
    isElectDrivetrain,
    isDiskBrakes,
    isHydrauric,
    isInternal,
    wheelBrandCd,
    wheelModel,
    bicycleDescribe,
    delNy,
    instDate,
    userCustomer_seq
) values (
	#{bicycleName}
	,#{bicycleCateCd}
	,#{bicycleBrandCd}
	,#{bicycleModel}
	,#{bicycleYears}
	,#{bicycleMiles}
	,#{bicycleSN}
	,#{drivetrainBrandCd}
	,#{drivetrainModel}
	,#{brakeBrandCd}
	,#{isElectDrivetrain}
	,#{isDiskBrakes}
	,#{isHydrauric}
	,#{isInternal}
	,#{wheelBrandCd}
	,#{wheelModel}
	,#{bicycleDescribe}
	,0
	,CURRENT_TIMESTAMP
	,#{userCustomer_seq}
)

	SELECT last_insert_id()
