mapper.insert:
insert into code (
	cdName
	,cdNameEng
	,cdUseNy
	,cdDesc
	,codegroup_cdgSeq
) values (
	#{cdName}
	,#{cdNameEng}
	,#{cdUseNy}
	,#{cdDesc}
	,#{codegroup_cdgSeq}
)

	SELECT last_insert_id()

mapper.update:
update code set
	cdName = #{cdName}
	,cdNameEng = #{cdNameEng}
	,cdUseNy = #{cdUseNy}
	,cdDesc = #{cdDesc}
	,codegroup_cdgSeq = #{codegroup_cdgSeq}
where
	cdSeq = #{cdSeq}
