mapper.sql:

	    FROM
		    reviews as r
			left join usercustomer as u on u.seq = r.userCustomer_seq
			left join shop as s on s.shopSeq = r.shop_shopSeq
		WHERE 1=1
		AND revwDelNy = #{shDelNy}

					AND revwDate BETWEEN #{shDateStart} AND #{shDateEnd}


            AND revwSeq = #{shValue}
            AND revwRate = #{shValue}
            AND u.userName LIKE CONCAT('%',#{shValue},'%')
            AND s.shopName LIKE CONCAT('%',#{shValue},'%')
            AND revwReviews LIKE CONCAT('%',#{shValue},'%')

mapper.select:
    	select count(*)

		AND shop_shopSeq = #{shopSeq}
		AND shop_shopSeq = #{shopSeq}
		AND revwDelNy = 0

mapper.update:
UPDATE reviews SET
	revwRate = #{revwRate}
	,revwReviews = #{revwReviews}
	,revwDate = DATE_FORMAT(CONVERT_TZ(NOW(), 'UTC', 'Asia/Seoul'), '%Y-%m-%d %H:%i:%S')
WHERE
	revwSeq = #{revwSeq}
