mapper.sql:

	    from shop as a
	    left join usercustomer b on a.userCustomer_seq = b.seq
	    where 1=1

        AND a.delNy = #{shDelNy}


					AND a.shopInstDate BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.shopUpdtDate BETWEEN #{shDateStart} AND #{shDateEnd}



            AND b.userId LIKE CONCAT('%',#{shValue},'%')
            AND a.shopName LIKE CONCAT('%',#{shValue},'%')
            AND a.shopAddress1 LIKE CONCAT('%',#{shValue},'%')
            AND a.shopTel LIKE CONCAT('%',#{shValue},'%')



        AND a.shopSeq IN 

            #{seq}

mapper.select:
select distinct s.shop_shopSeq ,a.shopSeq ,a.userCustomer_seq from shop as a left join availableService as s on s.shop_shopSeq = a.shopSeq left join handlingBicycleBrand as hb on a.shopSeq = hb.shop_shopSeq left join handlingWheelBrand as hw on a.shopSeq = hw.shop_shopSeq where 1=1 and bikeBrandCd = #{bikeBrandCd} and wheelBrandCd = #{wheelBrandCd} and parkingNy is not null and wifiNy is not null and cctvNy is not null and cafeNy is not null and petNy is not null and availableServiceCd in
#{availableServiceCd}
GROUP BY s.shop_shopSeq ,a.shopSeq ,a.userCustomer_seq HAVING COUNT(DISTINCT s.availableServiceCd) = ${availableServiceCd.size}

